import React from "react";
import { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import axios from "axios";
import {
  Form,
  FormGroup,
  Input,
  Label,
  Button,
  FormFeedback,
} from "reactstrap";

import * as Yup from "yup";

const Forms = () => {
  let [pizza, setPizza] = useState({
    name: "",
    size: null,
    hamur: "",
    nPepperoni: false,
    nDomates: false,
    nBiber: false,
    nSosis: false,
    nMisir: false,
    nSucuk: false,
    nAnanas: false,
    nTavukIzgara: false,
    nJalepeno: false,
    nSogan: false,
    nSarimsak: false,
    note: "",
  });

  const [formErrors, setFormErrors] = useState({
    name: "",
    size: null,
    hamur: "",
    nPepperoni: false,
    nDomates: false,
    nBiber: false,
    nSosis: false,
    nMisir: false,
    nSucuk: false,
    nAnanas: false,
    nTavukIzgara: false,
    nJalepeno: false,
    nSogan: false,
    nSarimsak: false,
    note: "",
  });

  const formSchema = Yup.object().shape({
    name: Yup.string().min(2, "Kullanıcı adı en az 2 karakterden oluşmalıdır."),
    size: Yup.string().required("Lütfen Pizzanızın boyutunu seçiniz!"),
    hamur: Yup.string().required("Lütfen Hamur kalınlığı tercihinizi seçiniz!"),
    note: Yup.string(),
    nPepperoni: Yup.string(),
    nDomates: Yup.string(),
    nBiber: Yup.string(),
    nSosis: Yup.string(),
    nMisir: Yup.string(),
    nSucuk: Yup.string(),
    nAnanas: Yup.string(),
    nTavukIzgara: Yup.string(),
    nJalepeno: Yup.string(),
    nSogan: Yup.string(),
    nSarimsak: Yup.string(),
  });
  // Buradaki dataları Array içinde tutalım.
  const inputChangeHandler = (e) => {
    const { name, value } = e.target;
    setPizza({ ...pizza, [name]: value });
    Yup.reach(formSchema, name)
      .validate(value)
      .then((valid) => {
        setFormErrors({ ...formErrors, [name]: "" });
      })
      .catch((err) => {
        setFormErrors({ ...formErrors, [name]: err.errors[0] });
      });
  };

  const inputCheckboxHandler = (e) => {
    const { name, checked } = e.target;
    setPizza({ ...pizza, [name]: checked });
  };

  useEffect(() => {
    console.log("pizza:", pizza);
  }, [pizza]);

  useEffect(() => {
    console.warn(formErrors);
  }, [formErrors]);

  const sender = useHistory();

  let [holdData, setHoldData] = useState([]);
  let [response, setResponse] = useState();

  return (
    <div>
      <Form
        id="pizza-form"
        onSubmit={(event) => {
          event.preventDefault();
          formSchema.isValid(pizza).then((valid) => {
