import React,{useState,useEffect} from "react";


const SiparisAdet = ({malzemeFunction}) =>{
    const[counter,setCounter]=useState(1);
    const arttir = () =>{
      setCounter(counter+1);
    }
    const azalt = () =>{
      setCounter(counter-1);
    }
  
    let mainKey = "pizzaAdet";
    useEffect(()=>{
      malzemeFunction(mainKey,counter);
    },[counter])
  
  
  return(
  
  <div>
  
  <button className="adetButton" onClick={azalt}disabled={counter===1 ? true:false}>-</button>
  <NumberText>{counter}</NumberText>
  <button className="adetButton" onClick={arttir}>+</button>
  
  </div>
  )
  }
  
  export default SiparisAdet;
